*, *:before, *:after {
  box-sizing: border-box;
}

input::-webkit-input-placeholder {
  color: #999;
}

input:focus::-webkit-input-placeholder {
  color: #333;
}

/*cancel input border radius*/
input, textarea {
  -webkit-appearance: none;
  -webkit-border-radius: 0;
}

body {
  font-size: 13px;
}

